import React, { useState, useEffect } from 'react';
import { 
  Container, 
  Typography, 
  Box, 
  Grid, 
  Card, 
  CardMedia,
  Button,
  IconButton,
  CardActionArea,
  Stack,
} from '@mui/material';
import { useSearch } from '../context/SearchContext';
import { useLocation } from 'react-router-dom';
import VerifiedIcon from '@mui/icons-material/Verified';
import KeyboardArrowLeft from '@mui/icons-material/KeyboardArrowLeft';
import KeyboardArrowRight from '@mui/icons-material/KeyboardArrowRight';

// Use the same NFT data as Home page
const allNFTs = [
  {
    id: 1,
    title: 'Abstract Thought of Art',
    creator: 'ZafGod.eth',
    price: '0.00069 ETH',
    image: 'https://via.placeholder.com/400x400/1a237e/ffffff',
    status: 'Now',
    isVerified: true
  },
  {
    id: 2,
    title: 'Harvested Opulence',
    creator: 'Fame Identity',
    price: '0.005 ETH',
    image: 'https://via.placeholder.com/400x400/4a148c/ffffff',
    status: '23 hours',
    isVerified: true
  },
  {
    id: 3,
    title: 'RELAX',
    creator: 'brain pasta',
    price: '0.0038 ETH',
    image: 'https://via.placeholder.com/400x400/311b92/ffffff',
    status: 'Now',
    isVerified: true
  },
  {
    id: 4,
    title: 'Spring will come',
    creator: 'Reza Milani',
    price: '0.0004 ETH',
    image: 'https://via.placeholder.com/400x400/6a1b9a/ffffff',
    status: 'Now',
    isVerified: true
  },
  {
    id: 5,
    title: 'Cyber Dreams',
    creator: 'neon.eth',
    price: '0.0089 ETH',
    image: 'https://via.placeholder.com/400x400/00838f/ffffff',
    status: 'Now',
    isVerified: true
  },
  {
    id: 6,
    title: 'Digital Wilderness',
    creator: 'artmaster',
    price: '0.0123 ETH',
    image: 'https://via.placeholder.com/400x400/2e7d32/ffffff',
    status: '2 hours',
    isVerified: true
  },
  {
    id: 7,
    title: 'Neon Nights',
    creator: 'pixelart.eth',
    price: '0.0075 ETH',
    image: 'https://via.placeholder.com/400x400/c2185b/ffffff',
    status: 'Now',
    isVerified: true
  },
  {
    id: 8,
    title: 'Future Past',
    creator: 'retro.eth',
    price: '0.0055 ETH',
    image: 'https://via.placeholder.com/400x400/d84315/ffffff',
    status: '5 hours',
    isVerified: true
  },
  {
    id: 9,
    title: 'Quantum Dreams',
    creator: 'quantum.art',
    price: '0.0095 ETH',
    image: 'https://via.placeholder.com/400x400/4527a0/ffffff',
    status: 'Now',
    isVerified: true
  },
  {
    id: 10,
    title: 'Virtual Reality',
    creator: 'vr.master',
    price: '0.0082 ETH',
    image: 'https://via.placeholder.com/400x400/00695c/ffffff',
    status: '12 hours',
    isVerified: true
  },
  {
    id: 11,
    title: 'Digital Genesis',
    creator: 'crypto.art',
    price: '0.0067 ETH',
    image: 'https://via.placeholder.com/400x400/bf360c/ffffff',
    status: 'Now',
    isVerified: true
  },
  {
    id: 12,
    title: 'Pixel Paradise',
    creator: 'pixel.master',
    price: '0.0043 ETH',
    image: 'https://via.placeholder.com/400x400/0277bd/ffffff',
    status: '8 hours',
    isVerified: true
  }
];

const ITEMS_PER_PAGE = 5;

const Marketplace = () => {
  const { searchTerm } = useSearch();
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const searchQuery = queryParams.get('search') || '';
  const [hoveredId, setHoveredId] = useState<number | null>(null);
  const [activeStep, setActiveStep] = useState(0);
  const [isAnimating, setIsAnimating] = useState(false);

  // Reset pagination when search query changes
  useEffect(() => {
    setActiveStep(0);
  }, [searchQuery]);

  // Filter NFTs based on search term with debounce
  const filteredNFTs = allNFTs.filter((nft) => {
    const searchLower = searchQuery.toLowerCase();
    return (
      nft.title.toLowerCase().includes(searchLower) ||
      nft.creator.toLowerCase().includes(searchLower) ||
      nft.price.toLowerCase().includes(searchLower)
    );
  });

  const maxSteps = Math.ceil(filteredNFTs.length / ITEMS_PER_PAGE);

  const handleNext = () => {
    if (isAnimating || activeStep >= maxSteps - 1) return;
    setIsAnimating(true);
    setActiveStep((prevStep) => prevStep + 1);
    setTimeout(() => setIsAnimating(false), 500);
  };

  const handleBack = () => {
    if (isAnimating || activeStep <= 0) return;
    setIsAnimating(true);
    setActiveStep((prevStep) => prevStep - 1);
    setTimeout(() => setIsAnimating(false), 500);
  };

  return (
    <Container maxWidth="xl" sx={{ mt: 4, pb: 8 }}>
      <Box sx={{ 
        display: 'flex', 
        justifyContent: 'space-between', 
        alignItems: 'center',
        mb: 4
      }}>
        <Typography variant="h5" component="h1" sx={{ fontWeight: 'bold' }}>
          NFT Marketplace
          {searchQuery && (
            <Typography variant="subtitle1" color="text.secondary" sx={{ mt: 1 }}>
              Search results for: {searchQuery}
            </Typography>
          )}
        </Typography>
      </Box>

      <Box sx={{ position: 'relative', width: '100%', overflow: 'hidden' }}>
        <IconButton
          onClick={handleBack}
          disabled={activeStep === 0 || isAnimating}
          sx={{
            position: 'absolute',
            left: -20,
            top: '50%',
            transform: 'translateY(-50%)',
            bgcolor: 'rgba(0, 0, 0, 0.5)',
            color: 'white',
            zIndex: 2,
            '&:hover': {
              bgcolor: 'rgba(0, 0, 0, 0.7)',
            },
            '&.Mui-disabled': {
              display: 'none'
            }
          }}
        >
          <KeyboardArrowLeft />
        </IconButton>

        <IconButton
          onClick={handleNext}
          disabled={activeStep >= maxSteps - 1 || isAnimating}
          sx={{
            position: 'absolute',
            right: -20,
            top: '50%',
            transform: 'translateY(-50%)',
            bgcolor: 'rgba(0, 0, 0, 0.5)',
            color: 'white',
            zIndex: 2,
            '&:hover': {
              bgcolor: 'rgba(0, 0, 0, 0.7)',
            },
            '&.Mui-disabled': {
              display: 'none'
            }
          }}
        >
          <KeyboardArrowRight />
        </IconButton>

        <Box
          sx={{
            display: 'flex',
            transition: 'transform 0.5s ease-in-out',
            transform: `translateX(-${activeStep * (100 / 5)}%)`,
            ml: 0,
          }}
        >
          {filteredNFTs.map((nft) => (
            <Box
              key={nft.id}
              sx={{
                flex: `0 0 ${100 / 5}%`,
                padding: '0 8px',
                transition: 'all 0.5s ease-in-out',
                opacity: 1,
                transform: 'scale(1)',
              }}
            >
              <Card 
                sx={{ 
                  bgcolor: 'rgba(22, 28, 36, 0.95)',
                  borderRadius: 2,
                  position: 'relative',
                  aspectRatio: '1/1',
                  cursor: 'pointer',
                  transition: 'transform 0.3s ease-in-out',
                  '&:hover': {
                    transform: 'scale(1.02)'
                  }
                }}
                onMouseEnter={() => setHoveredId(nft.id)}
                onMouseLeave={() => setHoveredId(null)}
              >
                <CardActionArea>
                  <CardMedia
                    component="img"
                    image={nft.image}
                    alt={nft.title}
                    sx={{ 
                      height: '100%',
                      width: '100%',
                      aspectRatio: '1/1'
                    }}
                  />
                  {hoveredId === nft.id && (
                    <Box
                      sx={{
                        position: 'absolute',
                        top: 0,
                        left: 0,
                        right: 0,
                        bottom: 0,
                        bgcolor: 'rgba(0, 0, 0, 0.5)',
                        display: 'flex',
                        justifyContent: 'center',
                        alignItems: 'center',
                        transition: 'all 0.3s ease-in-out'
                      }}
                    >
                      <Button
                        variant="contained"
                        sx={{
                          bgcolor: 'white',
                          color: 'black',
                          '&:hover': {
                            bgcolor: 'white',
                          },
                          borderRadius: '20px',
                          px: 3
                        }}
                      >
                        Buy Now
                      </Button>
                    </Box>
                  )}
                </CardActionArea>
                <Box sx={{ 
                  position: 'absolute', 
                  bottom: 0, 
                  left: 0, 
                  right: 0,
                  p: 2,
                  background: 'linear-gradient(to top, rgba(0,0,0,0.8) 0%, rgba(0,0,0,0) 100%)'
                }}>
                  <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                    <Typography variant="subtitle2" sx={{ color: 'grey.400' }}>
                      {nft.creator}
                    </Typography>
                    {nft.isVerified && (
                      <VerifiedIcon sx={{ fontSize: 16, color: '#2196f3' }} />
                    )}
                  </Box>
                  <Typography variant="subtitle1" sx={{ color: 'white', fontWeight: 'bold' }}>
                    {nft.title}
                  </Typography>
                  <Box sx={{ 
                    display: 'flex', 
                    justifyContent: 'space-between',
                    alignItems: 'center',
                    mt: 1 
                  }}>
                    <Box>
                      <Typography variant="caption" sx={{ color: 'grey.400' }}>
                        Minting
                      </Typography>
                      <Typography variant="body2" sx={{ color: 'white', fontWeight: 'bold' }}>
                        {nft.price}
                      </Typography>
                    </Box>
                    <Typography 
                      variant="caption" 
                      sx={{ 
                        color: nft.status === 'Now' ? '#4caf50' : 'white',
                        fontWeight: 'bold'
                      }}
                    >
                      â€¢ {nft.status}
                    </Typography>
                  </Box>
                </Box>
              </Card>
            </Box>
          ))}
        </Box>
      </Box>

      {filteredNFTs.length === 0 && (
        <Box sx={{ width: '100%', textAlign: 'center', mt: 4 }}>
          <Typography variant="h6" color="text.secondary">
            No NFTs found matching your search criteria
          </Typography>
        </Box>
      )}
    </Container>
  );
};

export default Marketplace;
