import React, { useState } from 'react';
import { 
  Container, 
  Typography, 
  Box, 
  Grid, 
  Card, 
  CardMedia,
  Button,
  IconButton,
  CardActionArea,
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import VerifiedIcon from '@mui/icons-material/Verified';
import KeyboardArrowLeft from '@mui/icons-material/KeyboardArrowLeft';
import KeyboardArrowRight from '@mui/icons-material/KeyboardArrowRight';

// Mock data for latest drops
const allNFTs = [
  {
    id: 1,
    title: 'Abstract Thought of Art',
    creator: 'ZafGod.eth',
    price: '0.00069 ETH',
    image: 'https://via.placeholder.com/400x400/1a237e/ffffff',
    status: 'Now',
    isVerified: true
  },
  {
    id: 2,
    title: 'Harvested Opulence',
    creator: 'Fame Identity',
    price: '0.005 ETH',
    image: 'https://via.placeholder.com/400x400/4a148c/ffffff',
    status: '23 hours',
    isVerified: true
  },
  {
    id: 3,
    title: 'RELAX',
    creator: 'brain pasta',
    price: '0.0038 ETH',
    image: 'https://via.placeholder.com/400x400/311b92/ffffff',
    status: 'Now',
    isVerified: true
  },
  {
    id: 4,
    title: 'Spring will come',
    creator: 'Reza Milani',
    price: '0.0004 ETH',
    image: 'https://via.placeholder.com/400x400/6a1b9a/ffffff',
    status: 'Now',
    isVerified: true
  },
  {
    id: 5,
    title: 'Cyber Dreams',
    creator: 'neon.eth',
    price: '0.0089 ETH',
    image: 'https://via.placeholder.com/400x400/00838f/ffffff',
    status: 'Now',
    isVerified: true
  },
  {
    id: 6,
    title: 'Digital Wilderness',
    creator: 'artmaster',
    price: '0.0123 ETH',
    image: 'https://via.placeholder.com/400x400/2e7d32/ffffff',
    status: '2 hours',
    isVerified: true
  },
  {
    id: 7,
    title: 'Neon Nights',
    creator: 'pixelart.eth',
    price: '0.0075 ETH',
    image: 'https://via.placeholder.com/400x400/c2185b/ffffff',
    status: 'Now',
    isVerified: true
  },
  {
    id: 8,
    title: 'Future Past',
    creator: 'retro.eth',
    price: '0.0055 ETH',
    image: 'https://via.placeholder.com/400x400/d84315/ffffff',
    status: '5 hours',
    isVerified: true
  },
  {
    id: 9,
    title: 'Quantum Dreams',
    creator: 'quantum.art',
    price: '0.0095 ETH',
    image: 'https://via.placeholder.com/400x400/4527a0/ffffff',
    status: 'Now',
    isVerified: true
  },
  {
    id: 10,
    title: 'Virtual Reality',
    creator: 'vr.master',
    price: '0.0082 ETH',
    image: 'https://via.placeholder.com/400x400/00695c/ffffff',
    status: '12 hours',
    isVerified: true
  },
  {
    id: 11,
    title: 'Digital Genesis',
    creator: 'crypto.art',
    price: '0.0067 ETH',
    image: 'https://via.placeholder.com/400x400/bf360c/ffffff',
    status: 'Now',
    isVerified: true
  },
  {
    id: 12,
    title: 'Pixel Paradise',
    creator: 'pixel.master',
    price: '0.0043 ETH',
    image: 'https://via.placeholder.com/400x400/0277bd/ffffff',
    status: '8 hours',
    isVerified: true
  }
];

const ITEMS_PER_PAGE = 5;

const Home = () => {
  const navigate = useNavigate();
  const [hoveredId, setHoveredId] = useState<number | null>(null);
  const [activeStep, setActiveStep] = useState(0);
  const maxSteps = Math.ceil(allNFTs.length / ITEMS_PER_PAGE);

  const handleNext = () => {
    setActiveStep((prevStep) => prevStep + 1);
  };

  const handleBack = () => {
    setActiveStep((prevStep) => prevStep - 1);
  };

  const currentNFTs = allNFTs.slice(
    activeStep * ITEMS_PER_PAGE,
    (activeStep + 1) * ITEMS_PER_PAGE
  );

  return (
    <Container maxWidth="xl" sx={{ mt: 4, pb: 8 }}>
      {/* Header Section */}
      <Box sx={{ 
        display: 'flex', 
        justifyContent: 'space-between', 
        alignItems: 'center',
        mb: 4
      }}>
        <Typography variant="h5" component="h1" sx={{ fontWeight: 'bold' }}>
          Latest drops
        </Typography>
      </Box>

      {/* NFT Grid with Navigation */}
      <Box sx={{ position: 'relative', width: '100%' }}>
        {/* Left Navigation Arrow */}
        <IconButton
          onClick={handleBack}
          disabled={activeStep === 0}
          sx={{
            position: 'absolute',
            left: -20,
            top: '50%',
            transform: 'translateY(-50%)',
            bgcolor: 'rgba(0, 0, 0, 0.5)',
            color: 'white',
            zIndex: 2,
            '&:hover': {
              bgcolor: 'rgba(0, 0, 0, 0.7)',
            },
            '&.Mui-disabled': {
              display: 'none'
            }
          }}
        >
          <KeyboardArrowLeft />
        </IconButton>

        {/* Right Navigation Arrow */}
        <IconButton
          onClick={handleNext}
          disabled={activeStep === maxSteps - 1}
          sx={{
            position: 'absolute',
            right: -20,
            top: '50%',
            transform: 'translateY(-50%)',
            bgcolor: 'rgba(0, 0, 0, 0.5)',
            color: 'white',
            zIndex: 2,
            '&:hover': {
              bgcolor: 'rgba(0, 0, 0, 0.7)',
            },
            '&.Mui-disabled': {
              display: 'none'
            }
          }}
        >
          <KeyboardArrowRight />
        </IconButton>

        {/* NFT Grid */}
        <Grid container spacing={2}>
          {currentNFTs.map((nft) => (
            <Grid item key={nft.id} xs={12} sm={6} md={12/5} lg={12/5}>
              <Card 
                sx={{ 
                  bgcolor: 'rgba(22, 28, 36, 0.95)',
                  borderRadius: 2,
                  position: 'relative',
                  aspectRatio: '1/1',
                  cursor: 'pointer',
                  transition: 'transform 0.3s ease-in-out',
                  '&:hover': {
                    transform: 'scale(1.02)'
                  }
                }}
                onMouseEnter={() => setHoveredId(nft.id)}
                onMouseLeave={() => setHoveredId(null)}
              >
                <CardActionArea>
                  <CardMedia
                    component="img"
                    image={nft.image}
                    alt={nft.title}
                    sx={{ 
                      height: '100%',
                      width: '100%',
                      aspectRatio: '1/1'
                    }}
                  />
                  {hoveredId === nft.id && (
                    <Box
                      sx={{
                        position: 'absolute',
                        top: 0,
                        left: 0,
                        right: 0,
                        bottom: 0,
                        bgcolor: 'rgba(0, 0, 0, 0.5)',
                        display: 'flex',
                        justifyContent: 'center',
                        alignItems: 'center',
                        transition: 'all 0.3s ease-in-out'
                      }}
                    >
                      <Button
                        variant="contained"
                        sx={{
                          bgcolor: 'white',
                          color: 'black',
                          '&:hover': {
                            bgcolor: 'white',
                          },
                          borderRadius: '20px',
                          px: 3
                        }}
                      >
                        Buy Now
                      </Button>
                    </Box>
                  )}
                </CardActionArea>
                <Box sx={{ 
                  position: 'absolute', 
                  bottom: 0, 
                  left: 0, 
                  right: 0,
                  p: 2,
                  background: 'linear-gradient(to top, rgba(0,0,0,0.8) 0%, rgba(0,0,0,0) 100%)'
                }}>
                  <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                    <Typography variant="subtitle2" sx={{ color: 'grey.400' }}>
                      {nft.creator}
                    </Typography>
                    {nft.isVerified && (
                      <VerifiedIcon sx={{ fontSize: 16, color: '#2196f3' }} />
                    )}
                  </Box>
                  <Typography variant="subtitle1" sx={{ color: 'white', fontWeight: 'bold' }}>
                    {nft.title}
                  </Typography>
                  <Box sx={{ 
                    display: 'flex', 
                    justifyContent: 'space-between',
                    alignItems: 'center',
                    mt: 1 
                  }}>
                    <Box>
                      <Typography variant="caption" sx={{ color: 'grey.400' }}>
                        Minting
                      </Typography>
                      <Typography variant="body2" sx={{ color: 'white', fontWeight: 'bold' }}>
                        {nft.price}
                      </Typography>
                    </Box>
                    <Typography 
                      variant="caption" 
                      sx={{ 
                        color: nft.status === 'Now' ? '#4caf50' : 'white',
                        fontWeight: 'bold'
                      }}
                    >
                      â€¢ {nft.status}
                    </Typography>
                  </Box>
                </Box>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Box>
    </Container>
  );
};

export default Home;
